/*
http://derrickrossignol.com
*/

:root {
    --colour: #2196F3;
    --searchBarBg: rgba(12,12,12,0.3);
}

/* Switch to COLOR VARIABLE */
.ytp-swatch-background-color, .ytp-volume-slider-handle::before, .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox, .ytp-scrubber-button, .ytp-volume-slider-handle, .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox::after, .ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, paper-button.ytd-subscribe-button-renderer, .ytp-chrome-controls .ytp-button[aria-pressed]::after, paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button, #progress.yt-page-navigation-progress, ytd-button-renderer.style-primary[is-paper-button], ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer {
	background-color: var(--colour);
}
.ytp-swatch-color, ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer, ytd-guide-entry-renderer[is-guide-refresh][active] .guide-icon.ytd-guide-entry-renderer, .guide-entry-badge.ytd-guide-entry-renderer, .badge-style-type-red.ytd-badge-supported-renderer, #link-list-container a, .description a, #content-text a, .sbpqs_a, ytd-toggle-button-renderer.style-default-active[is-icon-button], #confirm-button.yt-confirm-dialog-renderer, paper-button.ytd-subscribe-button-renderer[subscribed=""] .deemphasize.yt-formatted-string {
    color: var(--colour) !important;
}
.ytp-volume-slider-handle::after {
    z-index: -1;
}
ytd-topbar-logo-renderer.style-scope:nth-child(4) > a:nth-child(1) > div:nth-child(1) > svg:nth-child(1) > g:nth-child(1) > g:nth-child(1) path, ytd-topbar-logo-renderer.style-scope:nth-child(2) > a:nth-child(1) > div:nth-child(1) > svg:nth-child(1) > g:nth-child(1) > g:nth-child(1) > path:nth-child(1) {
    fill: var(--colour) !important;
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    background: var(--colour);
    opacity: 1;
}
paper-button[subscribed].ytd-subscribe-button-renderer {
    background: transparent;
    border: 2px solid var(--colour);
    color: var(--colour) !important;
    transition: 0.1s ease;
}
.deemphasize.yt-formatted-string {
    color: var(--colour);
    transition: 0.1s ease;
}
paper-button[subscribed].ytd-subscribe-button-renderer:hover {
    background: var(--colour);
    color: #fff !important;
}
paper-button[subscribed].ytd-subscribe-button-renderer:hover .deemphasize.yt-formatted-string, paper-button.ytd-subscribe-button-renderer .deemphasize.yt-formatted-string {
    color: #fff !important;
}







/* CHANGES */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    box-shadow: none;
    border-color: transparent;
    padding: 10px 15px;
    box-sizing: border-box;
    border-radius: 5px 0 0 5px;
    background: var(--searchBarBg);
}
#search-icon-legacy.ytd-searchbox {
    background: var(--searchBarBg);
    border-color: transparent;
    border-radius: 0 5px 5px 0;
}
#search-icon-legacy.ytd-searchbox:hover {
    background: var(--searchBarBg) !important;
    border-color: transparent !important;
}
#search-form {
    border-radius: 5px;
}
#search[has-focus=""] #container {
    border-color: transparent;
    box-shadow: none;
}
#search[has-focus=""] #search-form {
    border: 1px solid var(--colour);
}
#links-holder.ytd-c4-tabbed-header-renderer {
    top: -36px;
}
ytd-video-primary-info-renderer {
    border-bottom: none;
}
#sentiment.ytd-video-primary-info-renderer {
    width: 99.5% !important;
}
ytd-button-renderer.style-primary[disabled][is-paper-button] {
    background: rgba(200,200,200,0.1)
}
ytd-button-renderer.style-suggestive[is-paper-button] {
    background: transparent;
    border-color: var(--colour);
    color: var(--colour);
}
ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background: var(--colour);
    color: #fff;
}
ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer[subscribed=""] {
    background: #eee;
    color: #222;
}
#guide-wrapper {
    margin-right: -18px;
}
app-drawer #contentContainer {
    width: 240px !important;
    overflow-x: hidden;
}
#toggle.ytd-grid-renderer {
    background: rgba(50,50,50,0.5);
    padding: 10px 18px;
    color: #eee;
    transition: 0.1s ease;
}
#toggle.ytd-grid-renderer:hover {
    background: rgba(50,50,50,0.7)
}
#search-input.ytd-searchbox-spt input {
    color: #eee;
}
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:focus, ytd-searchbox[mode="legacy-centered"] #search-icon-legacy.ytd-searchbox:focus {
    background: #1c1c1c;
}
.sbfl_b {
    background: transparent;
}



/* ===== SETTINGS ===== */
/* To change these go to the style page */

ytd-thumbnail-overlay-resume-playback-renderer:before {
    content: "WATCHED";
    position: absolute;
    z-index: 100;
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    top: 0px;
    font-size: 18px;
    transition: 0.1s ease;
    color: #fff !important;
}
ytd-compact-video-renderer:hover #overlays, ytd-video-renderer:hover #overlays, ytd-grid-video-renderer:hover #overlays, ytd-playlist-video-renderer:hover #overlays {
    opacity: 0;
}
ytd-thumbnail #overlays {
    height: 100%;
    transition: 0.2s ease;
}
ytd-thumbnail-overlay-resume-playback-renderer {
    position: relative;
    bottom: initial;
    height: 100%;
    background: rgba(55,55,55,0.5)
}
ytd-thumbnail-overlay-resume-playback-renderer {
    opacity: 1;
}
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background: var(--colour);
    opacity: 0.5;
}

.sbsb_a, .sbdd_b {
    background: #222 !important;
    border-color: #222 !important;
}
.sbqs_a, .sbqs_c {
    color: #eee;
}
.sbsb_d {
    background: #2c2c2c;
}

body {
    background: #20252a;
    color: #FFF;
}

/* 

	HEADER 

*/
.navbar {
    background: #343b41;
    border: none;
    border-top: 4px solid #577289;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2);
}
.navbar-brand,
.navbar-nav > li > a {
    color: #DDD !important;
}
.navbar-brand:hover,
.navbar-nav > li > a:hover {
    color: #FFF !important;
}
.navbar-inverse .navbar-nav > .active > a,
.navbar-inverse .navbar-nav > .active > a:hover,
.navbar-inverse .navbar-nav > .active > a:focus {
    background: #577289;
}

/* HEADER: Dropdown menus */
.open > a {
    background: rgba(0, 0, 0, 0.3) !important;
}
.navbar-nav > li > .dropdown-menu,
.navbar-right .dropdown-menu {
    background: #343b41;
    margin-top: 22px;
    border-radius: 6px;
    border: none;
    overflow: visible !important;
    padding: 3px;
    left: 0;
    right: auto;
}
.navbar-nav > li > .dropdown-menu {
    margin-top: 12px;
}
.dropdown-menu::before {
    content: '';
    width: 0;
    height: 0;
    border: 8px solid transparent;
    position: absolute;
    top: -16px;
    left: 25px;
    border-bottom: 8px solid #343b41;
}
.navbar-nav > li > .dropdown-menu a,
.dropdown-menu li a {
    padding: 6px 10px;
    border-radius: 6px;
    margin: 3px;
    color: #DDD;
}
.dropdown-menu li a[href="/logout"] {
    background: #ee5050;
    color: #FFF;
}
.dropdown-menu li a[href="/logout"]:hover {
    background: #eb6b6b;
    color: #FFF;
}
.dropdown-menu li a:hover {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF;
}
.dropdown-menu li a:focus {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF;
    outline: 0;
}
.dropdown-menu::-webkit-scrollbar {
    width: 0px;
}
.dropdown-menu > .active > a,
.dropdown-menu > .active > a:hover,
.dropdown-menu > .active > a:focus {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF !important;
}

/* HEADER: Search and filters */
.input-group-btn:first-child > .btn,
.input-group-btn:first-child > .btn-group,
.btn-group {
    margin-right: 4px;
}
.btn-group.open .dropdown-toggle {
    box-shadow: none;
}
.bootstrap-select {
    border: none!important;
}
.bootstrap-select button {
    border: 1px solid rgba(0, 0, 0, 0.1) !important;
}
.navbar-form .dropdown-toggle {
    background: rgba(0, 0, 0, 0.25);
    border: none;
    border-radius: 4px !important;
    color: #FFF;
}
.dropdown-toggle.btn-default:hover,
.dropdown-toggle.btn-default:focus,
.dropdown-toggle.btn-default:active,
.dropdown-toggle.btn-default:active:hover {
    background: rgba(0, 0, 0, 0.35) !important;
    color: #FFF;
}
.bootstrap-select .dropdown-toggle:focus {
    outline: 0 !important;
}
.open > .dropdown-toggle.btn-default,
.open > .dropdown-toggle.btn-default:focus,
.btn-default:focus,
.btn-default.focus {
    background: rgba(0, 0, 0, 0.35) !important;
    color: #FFF !important;
}
.open > .dropdown-toggle.btn-default:hover {
    background: rgba(0, 0, 0, 0.35);
    color: #FFF;
}
.form-control.search-bar {
    background: rgba(0, 0, 0, 0.25);
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 4px !important;
    color: #FFF;
    padding: 6px 12px;
    height: auto;
    margin-right: 3px;
}
.form-control.search-bar:focus {
    background: rgba(0, 0, 0, 0.35);
    box-shadow: none;
}
.input-group-btn:last-child > .btn {
    margin-left: 5px;
    border-radius: 4px;
}
.btn-primary,
.btn-success,
.btn-default {
    border: 1px solid rgba(0, 0, 0, 0.5) !important;
    color: #FFF;
    background: rgba(215, 235, 255, 0.2);
    outline: 0 !important;
}
.btn-primary:focus,
.btn-success:focus,
.btn-default:focus {
    background: rgba(215, 235, 255, 0.2);
}
.btn-primary:hover,
.btn-success:hover,
.btn-default:hover {
    color: #FFF;
    background: rgba(215, 235, 255, 0.30);
}
.btn-primary:active,
.btn-success:active,
.btn-default:active {
    color: #FFF;
    background: rgba(215, 235, 255, 0.30)!important;
    box-shadow: none;
}
.btn-primary:active:hover,
.btn-success:active:hover,
.btn-default:active:hover {
    color: #FFF;
    box-shadow: none !important;
}
.dropdown-menu .divider {
    margin: 0px 11px;
    background: rgba(0, 0, 0, 0.3);
}
.glyphicon {
    top: 10%;
}

/* 

	BODY 

*/
.alert-info {
    background: rgba(117, 185, 255, .1) !important;
    border: 1px solid rgba(117, 185, 255, .1) !important;
    color: #FFF !important;
}
.table,
.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td {
    border: none;
}
.table {
    margin-bottom: 0px;
    overflow: hidden;
    background: rgba(0, 0, 0, 0.2);
}
.table-responsive {
    border-radius: 5px;
    border: 1px solid rgba(0, 0, 0, 0.4);
}
.table thead {
    background: none;
    color: #FFF;
    border-bottom: 2px solid rgba(0, 0, 0, 0.4);
}

/* BODY: Torrents */
.table tbody tr {
    border-top: 1px solid rgba(0,0,0,0.4);
    padding: 5px;
}
.table tbody tr * {
    background: none !important;
    color: #FFF;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.7);
}
.table-hover > tbody > tr:hover {
    background: none;
}
.table-striped > tbody > tr.default {
    background: rgba(215, 235, 255, 0.1) !important;
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background: rgba(215, 235, 255, 0.05) !important;
}
.table-striped > tbody > tr.default:hover {
    background: rgba(215, 235, 255, 0.1) !important;
}
.table-striped > tbody > tr:nth-of-type(odd):hover {
    background: rgba(215, 235, 255, 0.05) !important;
}
.table-striped > tbody > tr.success,
.panel-success .panel-heading {
    background: #6ad56e61 !important;
}
.table-striped > tbody > tr.success:hover,
.panel-success .panel-heading:hover {
    background: #6ad56e61 !important;
}
.table-striped > tbody > tr.danger,
.panel-danger .panel-heading {
    background: #eed5626e !important;
}
.table-striped > tbody > tr.danger:hover,
.panel-danger .panel-heading:hover {
    background: #eed5626e !important;
}

/* BODY: Inset stuff */
table.torrent-list tbody tr td a:visited,
.pagination-page-info {
    color: #FFF;
}
.table tbody tr a.comments {
    background: rgba(0, 0, 0, 0.5) !important;
    color: #FFF;
    border: none;
    margin-top: 1px;
}
.text-center[style="color: green;"] {
    color: #3F3 !important;
}
.text-center[style="color: red;"] {
    color: #F33 !important;
}
.fa-download:hover {
    color: #3F3;
}
.fa-magnet:hover {
    color: #F33;
}

/* BODY: Torrent Pages */
.panel {
    background: rgba(0, 0, 0, 0.2);
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
    border: none;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.25);
}
.panel-heading {
    border: none;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
}
.panel-default .panel-heading {
    border: none;
    background: rgba(215, 235, 255, 0.15);
    color: #FFF;
}
.panel-success .panel-heading {
    color: #CEC;
}
.panel-danger .panel-heading {
    color: #FDB;
}
.panel-body {
    color: #FFF;
}
.panel-footer {
    background: rgba(215, 235, 255, 0.10);
    color: #FFF;
    border: none;
}
.panel-footer a i:hover {
    color: inherit;
}
#comments {
    padding-bottom: 10px;
}
.comment-box {
    margin-top: 20px;
}
#comment,
body > .container input:not(.btn),
textarea,
select.form-control {
    background: rgba(215, 235, 255, 0.1) !important;
    border: 1px solid rgba(215, 235, 255, 0.1) !important;
    color: #FFF !important;
}
#comment:focus,
body > .container input:not(.btn):focus,
textarea:focus,
select.form-control:focus {
    outline: 0 !important;
    color: #FFF;
    border: 1px solid rgba(215, 235, 255, 0.1) !important;
    box-shadow: none !important;
    background: rgba(215, 235, 255, 0.15) !important;
}
#comments .btn-sm {
    float: right;
    padding: 10px 20px;
}
.alert-warning {
    background: #f3e1833b;
    border-color: #f3e1833b;
    color: #f3e183;
    margin: 5px;
}

/*

	PAGINATION 

*/
.pagination li a {
    margin: 5px;
    margin-top: 0px;
    border-radius: 3px;
    background: rgba(215, 235, 255, 0.2);
    color: #EEE;
    border: none;
}
.pagination li a:focus {
    background: rgba(215, 235, 255, 0.2);
    color: #EEE;
}
.pagination li a:hover {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination li.active a {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination li.active a:hover {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination .active a:focus {
    background: rgba(215, 235, 255, 0.5);
}
.pagination li.disabled a {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li.disabled a:hover {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li.disabled a:focus {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li:first-child a,
.pagination li:last-child a {
    padding: 2px 8px;
    margin-top: 4px;
}

/* RESPONSIVE FIXES */
@media (max-width: 1228px) {
    .navbar-nav > li > .dropdown-menu,
    .navbar-right .dropdown-menu {
        left: auto;
        right: 0;
    }
    .dropdown-menu::before {
        right: 25px;
        left: auto;
    }
}

/* GENERAL ELEMENTS */
a {
    color: #a4c6f6;
}
a:hover {
    color: #c9e0fc;
}
kbd,
code,
pre {
    border: none;
    color: #d1e6f8;
    background: rgba(215, 235, 255, 0.1);
    box-shadow: none;
}
option {
