}

/*Variables*/
:root {
--bg: #fffff4;                  
--accent: #693232;
--dubs: #ffdd1a;
--greentext: #789922;
--logo: url(https://i.imgur.com/1KYBwWl.png);
--mascot:url(https://i.imgur.com/JHAFlqL.png);
}  

/*Body & global things */
* {
    text-decoration: none !important;
    line-height: 15px;
    font-family: Monospace, Arial, sans-serif !important;

}

body {
    width: 100vw !important;
}

hr {
    border: none !important;
}

.boardBanner,
#scroll-marker,
.boardTitle,
body {
    background-color: var(--bg) !important;
    background-image: var(--mascot) !important;
    background-repeat: no-repeat;
