#thread-watcher {
    border: 1px solid var(--accent) !important;  
    box-shadow: none !important;
}

#watched-threads {
    background: var(--bg) !important;
    border: 0 !important;
}

#watched-threads>.dead-thread>a,
#watched-threads>.replies-unread>a {
    color: var(--accent) !important;
}

div.dead-thread,
.dead-thread {
    opacity: .4 !important;
}

/* menus */
#menu,
.submenu,
#fourchanx-settings {
    color: var(--accent) !important;
    border-radius: 0 !important;
}

/* front Page */

/* board list */
.box-inner {
    background: var(--bg) !important;
    color: var(--accent) !important;
    padding: 0 !important;
    border-left: .5px solid var(--accent) !important;
}

.top-box {
    background: 0 !important;
}

.boxbar {
    background: 0 !important;
    color: var(--accent);
    text-align: center;
    margin-top: 10px;
    margin-bottom: 10px;
}

li>a {
    color: var(--accent) !important;
}

.box-outer {
    border: 1px solid var(--accent) !important;
}

.warning {
    color: var(--accent) !important;
}

.c-thread img {
    border: 1px solid var(--accent) !important;
}

/* links at the bottom */

#ft li.current,
#ft li.fill {
    background: none !important;
    color: transparent !important;
}

#ft li {
    background-color: var(--bg) !important;
    border-color: var(--accent);
}

#ft ul {
    border-top: 0;
}

.stat-cell {
    display: inline;
    margin-right: 40px;
}

/* internal archive */
.postblock {
    padding: 5px;
    background-color: var(--bg);
    color: var(--accent);
    border-color: var(--accent);
}

table.flashListing tr:nth-of-type(odd) {
    background-color: transparent;
}

td {
    background-color: var(--bg);
}

/* 4chan x gallery */
#a-gallery {
    background-color: #000000de;
}

a.gal-start,
a.gal-stop,
.gal-buttons>a.menu-button,
a.gal-close,
.gal-count,
a.gal-name,
.gal-thumbnails {
    color: var(--bg) !important;
}

.gal-name:hover,
.gal-buttons a:hover {
    color: var(--dubs) !important;
}

.gal-prev,
.gal-next {
    display: none;
}

:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-buttons,
:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-name,
:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-count {
    right: 154px !important;
}

.gal-thumbnails {
    border-left: .7px solid var(--bg) !important;
    background: 0 !important;
}

.gal-highlight {
    background: 0 !important;
}

.gal-highlight>img,
.gal-highlight>video {
    background: 0 !important;
    padding: 4px;
    border: 1px solid var(--bg);
}

.gal-fit-width .gal-image img,
.gal-fit-width .gal-image video {
    max-width: calc(100% - 10px) !important;
}

.gal-image img,
.gal-image video {
    margin-left: 4px !important;
    margin-right: 4px !important;
}

/* various elements */
.right-box,
.left-box,
.content h3{
    background-color: var(--bg);
}

.content h3 {
    border-color: var(--accent);
}

hr,
.left-box hr,
.right-box hr {
    background-color: var(--accent) !important;
    color: var(--accent);
}

dt.first,
.boxcontent dd,
dt {
    color: var(--accent);
    border-color: var(--accent);
}

#announce,
#disclaimer-dialog {
    background-color: var(--bg);
    color: var(--accent);
    box-shadow: none;
}

#disclaimer-dialog .hd {
    border-bottom: 1px solid var(--accent) !important;
    background: none;
    padding: 3px;
    font-size: 9pt;
}

button {
    background: var(--bg);
    border: 1px solid var(--accent);
    padding: 3px;
    color: var(--accent);
}

h1 {
    letter-spacing: 1.5px;
    line-height: 23px;
}

/* catalog */

:root:not(.werkTyme) .catalog-thread.watched .catalog-thumb,
:root:root.werkTyme .catalog-thread.watched:not(:hover),
:root:root.werkTyme:not(.catalog-hover-expand) .catalog-thread.watched,
:root.werkTyme.catalog-hover-expand .catalog-thread.watched > .catalog-container:hover > .catalog-post {
    border: none !important;
}

.catalog-stats {
    color: var(--accent);
}

.postContainer.catalog-container,
.thread.catalog-thread {
    max-width: 300px !important;
    margin: 0 10px 10px 5px !important;
    background-color: var(--bg);
    border-radius: 5px;
}

.thread.catalog .postInfo.desktop,
.thread.catalog-thread .postMessage {
    text-align: center !important;
    margin-top: 4px;
    margin-left: 0 !important;
}

.catalog-link {
    height: 160px;
}

.catalog-link>img {
    max-height: 90%;
    vertical-align: middle !important;
}

.catalog-thumb {
    box-shadow: none !important;
    outline: 1px solid var(--accent);
    border-radius: 0 !important;
}

:root.yotsuba-b.catalog-hover-expand .catalog-container:hover > .post,
:root.yotsuba.catalog-hover-expand .catalog-container:hover > .post {
    display: none;
}

.thread {
    width: 64vw !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }

  .post.reply  {
    width: 100%;
  }

input[type="checkbox"],
#blotter,
.page-num,
.sideArrows{
    display: none;
}

.post.reply {
    margin-bottom: 9px;
}

.filter-highlight.opContainer,
.filter-highlight > .reply {
    box-shadow: none;
    border-left: 2px solid darkblue;
}

.quoteLink,
.quotelink,
.deadlink {
    color: darkblue !important;
}

.logo_homepage  {
    width: 278px;
    height: 231px;
    background-image: url(https://files.catbox.moe/0juo4s.png) !important;
}

.header__logo {
    background-image: url(https://files.catbox.moe/6x4ymf.png);
    width: 57px;
    height: 57px;
    background-size: 57px 57px;
}

.tag-home__item {
    display: none;
}

