/*center*/
.thread {
    width: 64vw !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }

  .post.reply  {
    width: 100%;
  }

/*polish*/
input[type="checkbox"],
#blotter,
.page-num,
.sideArrows{
    display: none;
}

.brackets-wrap::before,
.brackets-wrap::after {
    content: "" !important;
}

#scroll-marker {
    opacity: 0;
    box-shadow: none !important;
}

.post.reply {
    border: 0;
    margin-top: 10px;
    margin-bottom: 10px;
    box-shadow: 0 0 35px var(--onealt);
    padding: 15px;
}

hr {
    opacity: 0;
}

#header-bar{
    border: 0;
    opacity: .86;
    transition: opacity .3s;
}

#header-bar:hover {
    opacity: 1;
    transition: opacity;
}

